import React, { useState, useEffect } from "react";
import axios from "axios";
import { TextField } from "@material-ui/core";

const style = {
  container: {
    display: "flex",
    margin: "30px",
    flexDirection: "column",
    Color: "red",
    gap: "8px",
  },
};

function Prod() {
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:8000/todos")
      .then((res) => {
        // setTodos([res.data]);
        setTodos(res.data);
        console.log(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleChange = (event) => {
    const { value } = event.target;
    setText(value);
  };
  const handleSubmit = (event) => {
    const { code } = event;
    if (code === "Enter") {
      const newItem = {
        name: text,
        price: text,
      };
      setTodos((prev) => [newItem, ...prev]);
      setText("");
    }
  };
  return (
    <div>
      <h1> Todos List</h1>
      <label>Name : </label>
      <TextField
        id="outlined-basic"
        placeHolder="new item"
        variant="outlined"
        size="small"
        value={text}
        onChange={handleChange}
        onKeyPress={handleSubmit}
      />
      <div style={style.container}>
        {todos.map((todo) => (
          <div key={todo._id}>
            id : <span>{todo._id} ,</span> name : <span>{todo.title} ,</span>
            Price : <span>{todo.price}</span>
          </div>
        ))}
      </div>
    </div>
  );
}
export default Prod;
