import React, { useState, useEffect } from "react";
import "../css/todo.css";
import axios from "axios";
// import FontAwesomeIcon from "@fortawesome/fontawesome-svg-core";
import DeleteIcon from "@mui/icons-material/Delete";
function Todo() {
  const [text, setText] = useState("");
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    axios
      .get("https://jsonplaceholder.typicode.com/todos")
      .then((res) => {
        const { data } = res;
        data.length = 15;
        console.log(data);
        setTodos(data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const handleChange = (event) => {
    const value = event.target.value;
    setText(value);
  };

  const addTodo = () => {
    if (text === "") {
      alert("Write something first");
    } else {
      const newTodo = {
        id: Date.now(),
        title: text,
      };

      setTodos(() => [newTodo, ...todos]);
      setText("");
    }
  };
  const handleSave = (e) => {
    const { code } = e;
    if (code === "Enter") {
      const newTodo = {
        id: Date.now(),
        title: text,
      };
      if (text === "") {
        alert("Write something first");
      } else {
        setTodos((prevState) => [newTodo, ...prevState]);
        setText("");
      }
    }
  };

  const deleteTodo = (id) => {
    const updateTodo = [...todos].filter((todos) => todos.id !== id);
    setTodos(updateTodo);
    console.log(updateTodo);
  };
  return (
    <div>
      <h1 className="header">Todo List by using MERN</h1>

      <div className="input-methode">
        <input
          className="input-data"
          type="text"
          placeholder="write todo here"
          value={text}
          onChange={handleChange}
          onKeyDownCapture={handleSave}
        />
        <button className="addBtn" onClick={addTodo}>
          Add todo
        </button>
      </div>
      <div className="item-div">
        {todos.map((todo) => (
          <div className="item-list" key={todo.id}>
            <input type="checkbox" className="check" />
            <span>{todo.title}</span>
            <button className="delBtn" onClick={() => deleteTodo(todo.id)}>
              {/* <FontAwesomeIcon icon="fa-solid fa-trash-can" /> */}
              <svg data-testid="DeleteIcon"></svg>
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Todo;
